import Partido from "../model/Partido.js";

export default class PartidoControl{

    // #nome
    // #sigla
    // #numero


    //post
    gravar(request, response){
        if (request.method == "POST" && request.is("application/json")){

            const dados = request.body;
            const nome = dados.nome;
            const sigla = dados.sigla;
            const numero = dados.numero;
            

            if (nome && sigla && numero && preco && quantidade && descricao) {
                const Partido = new Partido(nome, sigla, numero, preco, quantidade, descricao)

                Partido.incluir().then(() => {
                    response.status(201).json({
                    "status": true,
                    "mensagem": "Cliente incluído com sucesso!"
    
                    })
                }).catch((erro) => {
                    response.status(500).json({
                        "status": false,
                        "mensagem": "Erro ao incluir o cliente: " + erro.message
                    });
                });
            }

           

        } else {
            response.status(405).json({
                "status": false,
                "mensagem": "Requisição inválida! Consulte a documentação da API"
            });
            
        }
    };

  
    

}
